contentCss:
body {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
  line-height: 1.4;
  margin: 1rem;

}

table {
  border-collapse: collapse;
}

table th,
table td {
  border: 1px solid #ccc;
  padding: .4rem;
}

figure {
  display: table;
  margin: 1rem auto;
}

figure figcaption {
  color: #999;
  display: block;
  margin-top: .25rem;
  text-align: center;
}

hr {
  border-color: #ccc;
  border-style: solid;
  border-width: 1px 0 0 0;
}

code {
  background-color: #e8e8e8;
  border-radius: 3px;
  padding: .1rem .2rem;
}

.mce-content-body:not([dir=rtl]) {
  blockquote {
    border-left: 2px solid #ccc;
    margin-left: 1.5rem;
    padding-left: 1rem;
  }
}

.tox-tbtn {
 border-radius: 10px;
}

// RTL
.mce-content-body[dir=rtl] {
  blockquote {
    border-right: 2px solid #ccc;
    margin-right: 1.5rem;
    padding-right: 1rem;
  }
}

initConfig:
tinymce.init({
  plugins: [
    "advlist autolink lists link image charmap print",
    "preview anchor searchreplace visualblocks code",
    "fullscreen insertdatetime media table paste"
  ],
  toolbar: "insertfile undo redo | styleselect | bold italic | alignleft aligncenter alignright alignjustify | bullist numlist outdent indent | link image",
  height: 360
});

lessVariables:
@background-color: #fff;
@toolbar-background-color: @background-color;
@menubar-background-color: @background-color;
@border-color: rgba(45, 115, 235, 0.3);
@color-black: rgba(34, 47, 62, 1);
@color-white: #fff;
@color-tint: rgba(47, 117, 236, 1);
@color-error: rgba(251, 72, 59, 1);
@color-success: rgba(78, 221, 111, 1);
@base-value: 16px;
@control-border-radius: 10px;
@panel-border-radius: 6px;
@tinymce-border-radius: 10px;
@font-stack: -apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Oxygen-Sans,Ubuntu,Cantarell,"Helvetica Neue",sans-serif;;
@font-weight-normal: normal;
@font-weight-bold: bold;
@toolbar-button-icon-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-text-color: contrast(@background-color, @color-white, @color-black);
@toolbar-button-hover-background-color: rgba(47, 117, 236, 1);
@toolbar-button-hover-icon-color: rgba(255, 255, 255, 1);
@toolbar-button-hover-text-color: rgba(255, 255, 255, 1);
@toolbar-button-focus-background-color: @toolbar-button-hover-background-color;
@toolbar-button-focus-icon-color: rgba(255, 255, 255, 1);
@toolbar-button-focus-text-color: rgba(255, 255, 255, 1);
@toolbar-button-active-background-color: @toolbar-button-hover-background-color;
@toolbar-button-enabled-background-color: @toolbar-button-hover-background-color;
@toolbar-button-active-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-active-text-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-icon-color: @toolbar-button-hover-icon-color;
@toolbar-button-enabled-text-color: @toolbar-button-hover-icon-color;
