input_title = false
input_content = false

class GetValue {
    get title() {
        return document.getElementById("title").value
    }
    get content() {
        this.value = tinymce.activeEditor.getContent()
        var regex = /(<([^>]+)>)/ig,
            body = this.value,
            result = body.replace(regex, "");
        // console.log(this.value + "\n" + result);
        return result
    }
}

function setType(type, length) {
    let value = new GetValue(type, length)
    tp = String(eval("value." + type))
    switch (type) {
        case 'title':
            input_title = checkInput(tp, length)
            // console.log('t>> ' + tp.length);
        case 'content':
            input_content = checkInput(tp, length)
            console.log('>> ' + tp.length);
        default:
            break
    }
}

function checkInput(type, length) {
    if (type != "" && type.length >= length) {
        return true
    } else
        return false
}

$(document).ready(function () {
    setTimeout(
        function() 
        {
            checkForm()
        }, 500);
})

$(document).keyup(function () {
    checkForm()
})

function checkForm() {
    setType("title", 3)
    setType("content", 300)
    
    console.log("FIELDS >>" + input_title + " and " + input_content);
    console.log("GOOD? >>" + input_title && input_content);
    
    if (input_title && input_content) {
        $("#btnSubmit").prop("disabled", false);
    } else {
        $("#btnSubmit").prop("disabled", true);
    }
}